mapper.delete:
delete
from inventory
where material_id = #{materialId,jdbcType=INTEGER}
  and factory_id = #{factoryId,jdbcType=INTEGER}

mapper.update:
update inventory
set count = #{count,jdbcType=DOUBLE}
where material_id = #{materialId,jdbcType=INTEGER}
  and factory_id = #{factoryId,jdbcType=INTEGER}

mapper.select:
select material_id, factory_id, count
from inventory
where material_id = #{materialId,jdbcType=INTEGER}
  and factory_id = #{factoryId,jdbcType=INTEGER}

mapper.update:
update inventory
set count = count + #{addCount}
where material_id = #{materialId,jdbcType=INTEGER}
  and factory_id = #{factoryId,jdbcType=INTEGER}
  and count = #{count,jdbcType=DOUBLE}
